const defaultState = {
    auth: true,
    role: 'storekeeper'
}

export const EXIT = 'EXIT'
export const ADMIN_ENTER = 'RESPONSIBLE_ENTER'
export const SALESMAN_ENTER = 'TECHER_ENTER'
export const STOREKEEPER_ENTER = 'MANAGER_ENTER'


export const userReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADMIN_ENTER:
            return { ...state, auth: true, role: 'admin' }
        case SALESMAN_ENTER:
            return { ...state, auth: true, role: 'salesman' }
        case STOREKEEPER_ENTER:
            return { ...state, auth: true, role: 'storekeeper' }
        case EXIT:
            return { ...state, auth: false}
        default: return state
    }
}
