import React from "react";
import { Col,Card, Button } from "react-bootstrap";
import { useSelector } from "react-redux";
import { createAccept } from "../../../http/acceptApi";
import { deleteStock } from "../../../http/stockApi";

const StockItem = ({ stock }) => {
    const addAccept = (id, title, amount) => {
        deleteStock(id)
        createAccept({title,amount })
    }
    return ( 
        <Col md={3}>
            <Card
                className="mt-2"
                style={{width: 200, height: 150}} border={'dark'}
            >
                <div
                    className="d-flex align-items-center justify-center flex-column"
                >
                    <div
                        style={{fontSize: 30}}
                        className="m-2"
                    >
                        Название
                    </div>
                    <div
                        style={{fontSize: 20}}
                    >
                        {stock.title}
                    </div>
                    <div

                        className="m-2"
                    >
                        Кол-во: {stock.amount}
                    </div>
                </div>
            </Card>
            <Button
                className="mt-2"
                style={{width: 200}}
                variant="dark"
                onClick={() => addAccept(stock.id, stock.title, stock.amount)}
            >
                Отправить в магазин
            </Button>

        </Col>
     );
}

export default StockItem;
