const defaultState = {
    stocks : [
        {id:1, name:'Мишка Кала', count: 50},
        {id:2, name:'Футболки', count: 5},
        {id:3, name:'Карандаши', count: 3000},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
    ]
}

export const ADD_STOCK= 'ADD_STOCK'



export const stockReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_STOCK:
            return { ...state, stocks:[...action.payload]}
        default: return state
    }
}
