import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { Button, Form, Modal, ModalBody, ModalFooter } from "react-bootstrap";
import { createProduct } from "../../../http/shopApi";
import { Card } from "react-bootstrap";

const InvoiceModal = ({ show, onHide }) => {
    const invoice = useSelector(state => state.invoice)

    return ( 
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Товары, которые требуется отправить в магазин
                </Modal.Title>
            </Modal.Header>
            <ModalBody>
                <div>
                    {invoice.needs.map(need => 
                        <Card
                            className="m-2"
                        >
                            <div
                                className="p-2"
                            >
                                Название: {need.title}
                                <br/>
                                Кол-во: {need.amount}
                            </div>
                            <Button
                                var
                            >
                                Отправленно
                            </Button>
                        </Card>    
                    )}
                </div>
            </ModalBody>
            <ModalFooter>
                <Button variant="outline-secondary" onClick={() => { onHide()}}>Закрыть</Button>
            </ModalFooter>
        </Modal>
     );
}


export default InvoiceModal;
