const defaultState = {
    needs : [
        {id:1, title:'Мишка Кала', amount: 50},
        {id:2, title:'Футболки', amount: 5},
        {id:3, title:'Карандаши', amount: 3000},
        {id:3, title:'Карандаши', amount: 3000},
        {id:3, title:'Карандаши', amount: 3000},
        {id:3, title:'Карандаши', amount: 3000},
        {id:3, title:'Карандаши', amount: 3000},

    ]
}

export const ADD_NEED = 'ADD_NEED'



export const invoiceReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_NEED:
            return { ...state, needs:[...action.payload]}
        default: return state
    }
}
