import React from "react";
import { Button, Container, Card, Form } from "react-bootstrap";
import { useState } from "react";
import { storekeeperEnterAction } from "../../store/action-creators/action-creators";

const AuthPage = () => {
    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')

    const check = (login, password) => {
        if (login === 'storekeeper' && password === 'admin') {
            dispatch(storekeeperEnterAction())
            navigate(DISCIPLINES_ROUTE)
        } else if (login === 'salesman' && password === 'admin') {
            dispatch(managerEnterAction())
            navigate(DISCIPLINES_ROUTE)
        } else {
            teacherCheck(login, password)
        }
    }

    return ( 
        <Container
            className="d-flex justify-content-center align-items-center vh-100"
        >
            <Card
                style={{ width: 600 }}
                className="d-flex  p-5"
            >
                <h1
                    className="m-auto"
                >
                    Авторизируйтесь
                </h1>
                <Form
                    className="d-flex flex-column"
                >
                    <Form.Control
                        className="mt-3"
                        placeholder="Введите ваш логин"
                    />
                    <Form.Control
                        className="mt-3"
                        type="password"
                        placeholder="Введите ваш пароль"
                    />
                </Form>
                <Button
                    className="mt-4"
                    variant="outline-secondary"
                >
                    Войти
                </Button>
            </Card>
        </Container>
     );
}

export default AuthPage;
