const defaultState = {
    products : [
        {id:1, name:'Мишка Кала', count: 50},
        {id:2, name:'Футболки', count: 5},
        {id:3, name:'Карандаши', count: 3000},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
        {id:4, name:'Хурма', count: 4},
    ]
}

export const ADD_PRODUCT = 'ADD_PRODUCT'



export const shopReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            return { ...state, products:[...action.payload]}
        default: return state
    }
}
